import React, { FC, useEffect, useState, useRef } from "react";
import {
    Card,
    CardBody,
    CardFooter,
    Typography,
    Input,
    Button,
} from "@material-tailwind/react";

interface IMessage {
    role: string;
    content: string;
}

const ChatGPT: FC = () => {
    const [messages, setMessages] = useState<IMessage[]>([]);
    const [input, setInput] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const messagesEndRef = useRef<null | HTMLDivElement>(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(scrollToBottom, [messages]);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (!input.trim()) return;

        const userMessage: IMessage = { role: "user", content: input };
        setMessages(prev => [...prev, userMessage]);
        setInput("");
        setIsLoading(true);

        const eventSource = new EventSource(`http://localhost:8000/api/chatbot/?message=${encodeURIComponent(input)}`);

        eventSource.onmessage = (event) => {
            const responseObject = JSON.parse(event.data);
            setMessages(prev => [
                ...prev,
                { role: "assistant", content: responseObject.content || "" }
            ]);
            setIsLoading(false);
        };

        eventSource.onerror = (error) => {
            console.log("Error with SSE connection:", error);
            eventSource.close();
            setIsLoading(false);
        };
    };

    return (
        <Card className="w-full max-w-[800px] mx-auto h-[80vh] flex flex-col">
            <CardBody className="flex-grow overflow-y-auto p-4">
                {messages.map((message, index) => (
                    <div
                        key={index}
                        className={`mb-4 ${message.role === "user" ? "text-right" : "text-left"
                            }`}
                    >
                        <Typography
                            variant="paragraph"
                            color={message.role === "user" ? "blue" : "gray"}
                            className={`inline-block p-3 rounded-lg ${message.role === "user"
                                    ? "bg-blue-100 text-blue-900"
                                    : "bg-gray-100 text-gray-900"
                                }`}
                        >
                            {message.content}
                        </Typography>
                    </div>
                ))}
                <div ref={messagesEndRef} />
            </CardBody>
            <CardFooter>
                <form onSubmit={handleSubmit} className="flex gap-2">
                    <Input
                        type="text"
                        placeholder="Type your message here..."
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        className="flex-grow"
                        disabled={isLoading}
                    />
                    <Button type="submit" disabled={isLoading}>
                        {isLoading ? "Thinking..." : "Send"}
                    </Button>
                </form>
            </CardFooter>
        </Card>
    );
};

export default ChatGPT;
