import React, { useState, useEffect } from 'react';
import { Line } from "react-chartjs-2";
import {
    Card,
    CardBody,
    CardFooter,
    Typography,
    Button,
} from "@material-tailwind/react";

interface Prediction {
    logistic: number;
    random_forest: number;
    xgboost: number;
}

interface Transaction {
    Time: number;
    Amount: number;
    V1: number;
    V2: number;
    V3: number;
    V4: number;
    V5: number;
    V6: number;
    V7: number;
    V8: number;
    V9: number;
    V10: number;
    V11: number;
    V12: number;
    V13: number;
    V14: number;
    V15: number;
    V16: number;
    V17: number;
    V18: number;
    V19: number;
    V20: number;
    V21: number;
    V22: number;
    V23: number;
    V24: number;
    V25: number;
    V26: number;
    V27: number;
    V28: number;
    Class?: number;  // The actual fraud label
}

const RealTimePrediction: React.FC = () => {
    const [predictions, setPredictions] = useState<Prediction | null>(null);
    const [transaction, setTransaction] = useState<Transaction | null>(null);

    useEffect(() => {
        const socket = new WebSocket("ws://localhost:8000/ws/fraud_detection/");

        socket.onopen = (event) => {
            console.log("WebSocket connection opened", event);
        };

        socket.onmessage = (event) => {
            console.log("WebSocket message received", event.data);
            const data = JSON.parse(event.data);
            if (data.predictions && data.transaction) {
                setPredictions(data.predictions);
                setTransaction(data.transaction);
            }
        };

        return () => {
            socket.close();
        };
    }, []);

    if (!predictions || !transaction) {
        return <div>Waiting for predictions...</div>;
    }

    return (
        <div>
            <h2>Real-Time Fraud Detection</h2>
            <h3>Transaction Details:</h3>
            <p>Amount: ${transaction.Amount.toFixed(2)}</p>
            <p>Time: {new Date(transaction.Time * 1000).toLocaleTimeString()}</p>
            {transaction.Class !== undefined && (
                <p>Actual Class: {transaction.Class === 1 ? 'Fraud' : 'Not Fraud'}</p>
            )}
            <h3>Predictions:</h3>
            <p>Logistic Regression: {predictions.logistic === 1 ? 'Fraud' : 'Not Fraud'}</p>
            <p>Random Forest: {predictions.random_forest === 1 ? 'Fraud' : 'Not Fraud'}</p>
            <p>XGBoost: {predictions.xgboost === 1 ? 'Fraud' : 'Not Fraud'}</p>
        </div>
    );
};

export default RealTimePrediction;
