import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import {
    Card,
    CardBody,
    CardFooter,
    Typography,
    Input,
    Button,
} from "@material-tailwind/react";

interface Transaction {
    id: string;
    amount: number;
    time: string;
}

interface Predictions {
    logistic: number;
    random_forest: number;
    xgboost: number;
}

const RealTimePrediction: React.FC = () => {
    const [predictions, setPredictions] = useState<Predictions | null>(null);
    const [transaction, setTransaction] = useState<Transaction | null>(null);

    useEffect(() => {
        const socket = new WebSocket("ws://localhost:8000/ws/fraud_detection/");

        socket.onmessage = (event) => {
            const data = JSON.parse(event.data);
            setPredictions(data.predictions);
            setTransaction(data.transaction);
        };

        return () => {
            socket.close();
        };
    }, []);

    return (
        <div className="flex justify-center items-center h-screen">
            {transaction && predictions ? (
                <Card placeholder="Loading..."
                    className="w-full max-w-[800px]">
                    <CardBody placeholder="Loading...">
                        <Typography placeholder="Loading..." variant="h5" className="mb-2 text-blue-700">
                            Real-Time Fraud Prediction
                        </Typography>
                        <Typography placeholder="Loading..." className="mb-4">
                            <strong>Transaction ID:</strong> {transaction.id}
                        </Typography>
                        <Typography placeholder="Loading..." className="mb-4">
                            <strong>Amount:</strong> ${transaction.amount}
                        </Typography>
                        <Typography placeholder="Loading..." className="mb-4">
                            <strong>Time:</strong> {transaction.time}
                        </Typography>

                        <div className="mb-6">
                            <Line
                                data={{
                                    labels: ["Logistic", "Random Forest", "XGBoost"],
                                    datasets: [
                                        {
                                            label: "Fraud Prediction (0 = No Fraud, 1 = Fraud)",
                                            data: [
                                                predictions.logistic,
                                                predictions.random_forest,
                                                predictions.xgboost,
                                            ],
                                            backgroundColor: [
                                                "rgba(75, 192, 192, 0.2)",
                                                "rgba(255, 159, 64, 0.2)",
                                                "rgba(153, 102, 255, 0.2)",
                                            ],
                                            borderColor: [
                                                "rgba(75, 192, 192, 1)",
                                                "rgba(255, 159, 64, 1)",
                                                "rgba(153, 102, 255, 1)",
                                            ],
                                            borderWidth: 2,
                                        },
                                    ],
                                }}
                                options={{
                                    scales: {
                                        y: {
                                            beginAtZero: true,
                                            ticks: {
                                                stepSize: 1,
                                            },
                                        },
                                    },
                                }}
                            />
                        </div>
                    </CardBody>
                    <CardFooter placeholder="Loading...">
                        <Button
                            placeholder="Loading..."
                            variant="gradient"
                            className="bg-blue-500 text-white w-full"
                            onClick={() => window.location.reload()}
                        >
                            Refresh
                        </Button>
                    </CardFooter>
                </Card>
            ) : (
                <Card placeholder="Loading..." className="w-full max-w-[800px]">
                    <CardBody placeholder="Loading...">
                        <Typography placeholder="Loading..." variant="h5" className="mb-2 text-blue-700">
                            Waiting for Predictions...
                        </Typography>
                        <Typography placeholder="Loading..." className="mb-4">
                            Waiting for new transaction data to predict fraud...
                        </Typography>
                    </CardBody>
                </Card>
            )}
        </div>
    );
};

export default RealTimePrediction;
