import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

interface Prediction {
    logistic: number;
    random_forest: number;
    xgboost: number;
}

interface Transaction {
    Time: number;
    Amount: number;
    V1: number;
    V2: number;
    V3: number;
    V4: number;
    V5: number;
    V6: number;
    V7: number;
    V8: number;
    V9: number;
    V10: number;
    V11: number;
    V12: number;
    V13: number;
    V14: number;
    V15: number;
    V16: number;
    V17: number;
    V18: number;
    V19: number;
    V20: number;
    V21: number;
    V22: number;
    V23: number;
    V24: number;
    V25: number;
    V26: number;
    V27: number;
    V28: number;
    Class?: number;  // The actual fraud label
}

interface RealTimeChartProps {
    predictions: Prediction[];
    transactions: Transaction[];
}

const RealTimeChart: React.FC<RealTimeChartProps> = ({ predictions, transactions }) => {
    const [chartData, setChartData] = useState<any[]>([]);
    const [fraudAlert, setFraudAlert] = useState<string | null>(null);

    useEffect(() => {
        console.log("Updating chart data and checking for fraud...");

        // Generate labels based on the transaction time
        const timeLabels = transactions.map(t => t.Time ? new Date(t.Time * 1000).toLocaleTimeString() : '');

        // Prepare data for the chart
        const newChartData = transactions.map((transaction, index) => ({
            time: timeLabels[index],
            logistic: predictions[index]?.logistic,
            random_forest: predictions[index]?.random_forest,
            xgboost: predictions[index]?.xgboost,
            amount: transaction.Amount,
        }));

        console.log("New chart data:", newChartData);
        setChartData(newChartData);

        // Check for fraud and set alert
        const latestTransaction = transactions[transactions.length - 1];
        if (latestTransaction && latestTransaction.Class === 1) {
            const alertMessage = `Fraud detected at ${new Date(latestTransaction.Time * 1000).toLocaleTimeString()}`;
            console.log(alertMessage);
            setFraudAlert(alertMessage);
        } else {
            setFraudAlert(null);
        }
    }, [predictions, transactions]);

    return (
        <div>
            <h2>Real-Time Predictions</h2>
            {fraudAlert && <div style={{ color: 'red', fontWeight: 'bold' }}>{fraudAlert}</div>}
            <LineChart
                width={600}
                height={300}
                data={chartData}
                margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="time" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="logistic" stroke="#8884d8" />
                <Line type="monotone" dataKey="random_forest" stroke="#82ca9d" />
                <Line type="monotone" dataKey="xgboost" stroke="#ffc658" />
            </LineChart>
            <h2>Transactions</h2>
            <ul>
                {transactions.map((transaction, index) => (
                    <li key={index}>
                        Time: {transaction.Time}, Amount: {transaction.Amount}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default RealTimeChart;
