import React, { useState, useEffect } from 'react';
import { LineChart, Line, Scatter, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Card, CardBody, Typography, Alert } from '@material-tailwind/react';

interface Prediction {
    logistic: number;
    random_forest: number;
    xgboost: number;
}

interface Transaction {
    Time: number;
    Amount: number;
    Class?: number;  // The actual fraud label
}

interface RealTimeChartProps {
    predictions: Prediction[];
    transactions: Transaction[];
}

const RealTimeChart: React.FC<RealTimeChartProps> = ({ predictions, transactions }) => {
    const [chartData, setChartData] = useState<any[]>([]);
    const [fraudAlert, setFraudAlert] = useState<string | null>(null);

    useEffect(() => {
        // Generate labels based on the transaction time
        const timeLabels = transactions.map(t => t.Time ? new Date(t.Time * 1000).toLocaleTimeString() : '');

        // Prepare data for the chart
        const newChartData = transactions.map((transaction, index) => ({
            time: timeLabels[index],
            logistic: predictions[index]?.logistic,
            random_forest: predictions[index]?.random_forest,
            xgboost: predictions[index]?.xgboost,
            amount: transaction.Amount,
        }));

        setChartData(newChartData);

        // Check for fraud and set alert
        const latestTransaction = transactions[transactions.length - 1];
        if (latestTransaction && latestTransaction.Class === 1) {
            const alertMessage = `üö® Fraud detected at ${new Date(latestTransaction.Time * 1000).toLocaleTimeString()}`;
            setFraudAlert(alertMessage);
        } else {
            setFraudAlert(null);
        }
    }, [predictions, transactions]);

    return (
        <div className="p-4 flex flex-col items-center">
            <Card placeholder={<div>Loading...</div>} className="w-full max-w-4xl mb-4 shadow-lg">
                <CardBody placeholder={<div>Loading...</div>}>
                    <Typography placeholder={<div>Loading...</div>} variant="h5" color="blue-gray" className="text-center mb-4">
                        Real-Time Fraud Detection
                    </Typography>

                    {fraudAlert && (
                        <Alert color="red" icon={<span>‚ö†Ô∏è</span>} className="mb-4 text-center">
                            {fraudAlert}
                        </Alert>
                    )}

                    <ResponsiveContainer width="100%" height={400}>
                        <LineChart data={chartData}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="time" />
                            {/* Left Y-Axis for Predictions */}
                            <YAxis yAxisId="left" domain={[0, 1]} label={{ value: 'Fraud Probability', angle: -90, position: 'insideLeft' }} />
                            {/* Right Y-Axis for Transaction Amount */}
                            <YAxis yAxisId="right" orientation="right" domain={[0, 'auto']} label={{ value: 'Amount ($)', angle: -90, position: 'insideRight' }} />

                            <Tooltip formatter={(value: any, name: string) => [value, name === 'amount' ? 'Amount ($)' : 'Prediction']} />
                            <Legend />

                            {/* Line for fraud prediction probabilities */}
                            <Line type="monotone" dataKey="logistic" stroke="#8884d8" yAxisId="left" name="Logistic Regression" />
                            <Line type="monotone" dataKey="random_forest" stroke="#82ca9d" yAxisId="left" name="Random Forest" />
                            <Line type="monotone" dataKey="xgboost" stroke="#ffc658" yAxisId="left" name="XGBoost" />

                            {/* Scatter for transaction amounts (right axis) */}
                            <Scatter name="Transaction Amount" dataKey="amount" fill="#00bfff" yAxisId="right" />
                        </LineChart>
                    </ResponsiveContainer>

                    <Typography placeholder={<div>Loading...</div>} variant="h6" className="mt-6 text-center">
                        Transaction History
                    </Typography>
                    <div className="mt-2 max-h-64 overflow-y-auto border border-gray-200 rounded-md p-4 bg-gray-50">
                        <ul>
                            {transactions.map((transaction, index) => (
                                <li key={index} className="text-sm text-gray-700 mb-1">
                                    <strong>Time:</strong> {new Date(transaction.Time * 1000).toLocaleTimeString()} -
                                    <strong> Amount:</strong> ${transaction.Amount.toFixed(2)}
                                </li>
                            ))}
                        </ul>
                    </div>
                </CardBody>
            </Card>
        </div>
    );
};

export default RealTimeChart;
